import React, {useState, useEffect, useRef} from 'react';
import {Button, Col, Container, Form, FormGroup, Input, Card, CardBody, ModalHeader, Label, InputGroup, InputGroupAddon, InputGroupText, CardHeader} from 'reactstrap';
import axios from '../../../helpers/axiosConfig';
import {getList} from 'helpers/ListHelper'; 
import SimpleModal from "../components/SimpleModal";

const MedicalAnamnesis = ({location}) => {
    const [has, setHas] = useState(false);

    const modalRef = useRef()
    const setModal = (options) => {
        modalRef.current.setModal(options);
    };

    //LOCATION###############################################################
    useEffect(() => {
        if (!location?.state?.id) { 
            location.state.id = 0;
        }
        fetchLastMedical(location.state.id);
    }, []);

    //FILES##################################################################
    const [imageName, setImageName] = useState("");
    const [file, setFile] = useState({});
    const [hasFile, setHasFile] = useState(false);
    const [images, setImages] = useState([]);

    const fetchImages = async () => {
        const res = await axios.post("photo/medico_geriatra/obtain", {anamnId: medical.geriatric_medical_anamnesis_id})
        if (!res.data.error) {
            setImages(res.data.result);
            setImageName("");
            setFile({});
            setHasFile(false);
        };       
    };

    const loadFile = (file) => {
        if (file[0]?.name) {
            let filename = file[0].name;
            filename = filename.slice(0, (filename.lastIndexOf('.')));
            // filename = filename.replace(/\s+/g, '')
            setImageName(filename);
            setFile(file);
        } else {
            setImageName("");
            setFile({});
        };
    };

    const saveFile = () => {
        if (imageName) {
            const f = new FormData();
            f.append("file", file[0], imageName + '.jpg');
            axios.post("file/img_medico/" + location.state.run, f)
            .then(response => {
                saveImage();
            }).catch(error => {
                console.log(error);
            });
        }
    };

    const saveImage = async () => {
        const res = await axios.post("photo/medico_geriatra",
            {
                anamnId : medical.geriatric_medical_anamnesis_id,
                nombreImagen: imageName + '.jpg',
                ruta: "static/images_anam_medico/" + location.state.run
            }
        );
        if (!res.data.error) {
            fetchImages();
        }; 
    };

    const removeImage = async (fId, name) => {
        const res = await axios.post("photo/medico_geriatra/rm/", {
            fId : fId,
            route : location.state.run + '/' + name
        });
        if (!res.data.error) {
            fetchImages();
        }; 
    }

    //CRUD###################################################################
    const saveChanges = async () => {
        try {
            const res = await axios.put("geriatrical-medical-anamnesis/crud", medical);
            if (!res.data.error) {
                console.log('Cambios guardados correctamente!');
            } else {
                console.log('Err: ' + res.data.result.code + ' ' + res.data.result.sqlMessage);
            };

        } catch(e) {
            console.log('Err: ' + e);
        };
    };

    const saveChangesFromButton = async e => {
        e.preventDefault();
        saveChanges();
    };    

    //GROUP#################################################################
    const [group, setGroup] = useState('fisical');
    const changeGroup = (group) => {
        var form = document.getElementById('id-form-update');
        if(form.checkValidity()) {
            saveChanges();
            setGroup(group);
        } else {
            document.getElementById('id-btn-save').click();
        };
        fetchImages();
    };

    //MEDICAL###############################################################
    const defaultMedical = {geriatric_medical_anamnesis_id: 0, person_id: 0, walking_speed: 0, weight: 0, heigth: 0, bmi: 0, blood_pressure: "", temperature: 0, heart_rate: 0, oxygen_saturation: 0, muscular_strength: "s/e", glycemia: 0, extra_info: "", folestein_mmse: 0, pfeiffer_test: 0, yesavage_test: 0};
    const [medical, setMedical] = useState(defaultMedical);
    
    const handleInputChange = e => {
        setMedical({
            ...medical,
            [e.target.name]: e.target.value
        });
    };

    const fetchLastMedical = async (person_id) => {
        const res = await getList("geriatrical-medical-anamnesis/crud/" + person_id);
        if (res.length === 1) {
            setMedical(res[0]);
            setHas(true);
        } else {
            setHas(false);
        };        
    };  

    //JSX####################################################################
    return (
        <Container>
            <h4 className="heading-small text-muted mb-2 mt-2">Anamnesis M√©dico-geri√°trica</h4>
            {!has 
                ? <Card><h4>El paciente no tiene registros en Anamnesis M√©dico-geri√°trica.</h4></Card> 
                : <Card>
                    <CardBody className="pb-0 pt-1">
                        {/* {location?.state?.run && <h3>{location.state.run} {location.state.name} {location.state.apellido}</h3>} */}
                        <ModalHeader className="pb-5 pl-0">
                            <a style={{paddingRight: "15px", cursor: "pointer", ...(group === 'fisical' ? {color:"#11cdef"} : {})}} onClick={() => changeGroup('fisical')}>Parametros fisicos</a>
                            <a style={{paddingRight: "15px", cursor: "pointer", ...(group === 'test' ? {color:"#11cdef"} : {})}} onClick={() => changeGroup('test')}>Evaluaciones</a>
                            <a style={{paddingRight: "15px", cursor: "pointer", ...(group === 'repo' ? {color:"#11cdef"} : {})}} onClick={() => changeGroup('repo')}>Repositorio</a>
                        </ModalHeader>

                        <Form id="id-form-update" onSubmit={(e) => {saveChangesFromButton(e)}} role="form">
                            {group === 'fisical' && <>
                                <FormGroup className="row">
                                    <Col>
                                        <Label className="form-control-label">Velocidad de marcha</Label>
                                        <InputGroup>
                                            <Input
                                                name="walking_speed"
                                                value={medical.walking_speed}
                                                onChange={e => handleInputChange(e)}
                                                type="number" step="0.1" autoComplete="off"
                                            />
                                            <InputGroupAddon addonType="append"><InputGroupText>m/s</InputGroupText></InputGroupAddon>
                                        </InputGroup>
                                    </Col>
                                    <Col>
                                        <Label className="form-control-label">Peso</Label>
                                        <InputGroup>
                                            <Input
                                                name="weight"
                                                value={medical.weight}
                                                onChange={e => handleInputChange(e)}
                                                type="number" step="1"  autoComplete="off"
                                            />
                                            <InputGroupAddon addonType="append"><InputGroupText>Kgs</InputGroupText></InputGroupAddon>
                                        </InputGroup>
                                    </Col>
                                    <Col>
                                        <Label className="form-control-label">Altura</Label>
                                        <InputGroup>
                                            <Input
                                                name="heigth"
                                                value={medical.heigth}
                                                onChange={e => handleInputChange(e)}
                                                type="number" step="0.01" autoComplete="off"
                                            />
                                            <InputGroupAddon addonType="append"><InputGroupText>ms</InputGroupText></InputGroupAddon>
                                        </InputGroup>
                                    </Col>
                                </FormGroup>
                                <FormGroup className="row">
                                    <Col>
                                        <Label className="form-control-label">Presi√≥n Arterial (Sist√≥lica / Diast√≥lica)</Label>
                                        <InputGroup>
                                            <Input
                                                name="blood_pressure"
                                                value={medical.blood_pressure}
                                                onChange={e => handleInputChange(e)}
                                                autoComplete="off" type="text"
                                            />
                                        <InputGroupAddon addonType="append"><InputGroupText>mmHg</InputGroupText></InputGroupAddon>
                                        </InputGroup>
                                    </Col>
                                    <Col>
                                        <Label className="form-control-label">Temperatura</Label>
                                        <InputGroup>
                                            <Input
                                                name="temperature"
                                                value={medical.temperature}
                                                onChange={e => handleInputChange(e)}
                                                type="number" step="1" autoComplete="off"
                                            />
                                            <InputGroupAddon addonType="append"><InputGroupText>¬∫C</InputGroupText></InputGroupAddon>
                                        </InputGroup>
                                    </Col>
                                    <Col>
                                        <Label className="form-control-label">Frecuencia Car√≠aca</Label>
                                        <InputGroup>
                                            <Input
                                                name="heart_rate"
                                                value={medical.heart_rate}
                                                onChange={e => handleInputChange(e)}
                                                type="number" step="1" autoComplete="off"
                                            />
                                            <InputGroupAddon addonType="append"><InputGroupText>lat/m</InputGroupText></InputGroupAddon>
                                        </InputGroup>
                                    </Col>
                                </FormGroup>
                                <FormGroup className="row">
                                    <Col>
                                        <Label className="form-control-label">Saturaci√≥n de Ox√≠geno</Label>
                                        <InputGroup>
                                            <Input
                                                name="oxygen_saturation"
                                                value={medical.oxygen_saturation}
                                                onChange={e => handleInputChange(e)}
                                                type="number" step="1" autoComplete="off"
                                            />
                                            <InputGroupAddon addonType="append"><InputGroupText>SpO2</InputGroupText></InputGroupAddon>
                                        </InputGroup>
                                    </Col>
                                    <Col>
                                        <Label className="form-control-label">Fuerza Muscular</Label>
                                        <InputGroup>
                                            <InputGroupAddon addonType="prepend"><InputGroupText>Grado</InputGroupText></InputGroupAddon>
                                            <Input
                                                name="muscular_strength"
                                                value={medical.muscular_strength}
                                                onChange={e => handleInputChange(e)}
                                                type="select" autoComplete="off"><option>s/e</option><option>1</option><option>2</option><option>3</option><option>4-</option><option>4</option><option>4+</option><option>5</option>
                                            </Input>
                                        </InputGroup>
                                    </Col>
                                    <Col>
                                        <Label className="form-control-label">Glicemia</Label>
                                        <InputGroup>
                                            <Input
                                                name="glycemia"
                                                value={medical.glycemia}
                                                onChange={e => handleInputChange(e)}
                                                type="number" step="1" autoComplete="off"
                                            />
                                            <InputGroupAddon addonType="append"><InputGroupText>mg/dl</InputGroupText></InputGroupAddon>
                                        </InputGroup>
                                    </Col>
                                </FormGroup>
                                <FormGroup className="row">
                                    <Col>
                                        <Label className="form-control-label">Informaci√≥n Adicional</Label>
                                        <Input
                                            type="textarea" autoComplete="off" maxLength="250"
                                            name="extra_info"
                                            value={medical.extra_info}
                                            onChange={e => handleInputChange(e)}
                                        />
                                    </Col>
                                </FormGroup>
                            </>}
                            {group === 'test' && <>
                                <Label className="form-control-label">Cuestionario Minimental de Folstein</Label>
                                <FormGroup className="row">
                                    <Col md="10">
                                        <Input
                                            type="number" autoComplete="off"
                                            name="folestein_mmse"
                                            value={medical.folestein_mmse}
                                            onChange={e => handleInputChange(e)}
                                        />
                                    </Col>
                                    <Col md="2">
                                        <Button 
                                            color="secondary" 
                                            href="https://docs.google.com/forms/d/e/1FAIpQLScOfJZpOKaTXd2OQ6RkgpvhByXY51RzeNtlH24rkiQJ1DCZiw/viewform"
                                            target="_blank">Cuestionario
                                        </Button>
                                    </Col>
                                </FormGroup>
                                <Label className="form-control-label">Test de Pfeiffer</Label>
                                <FormGroup className="row">
                                    <Col md="10">
                                        <Input
                                            type="number" autoComplete="off"
                                            name="pfeiffer_test"
                                            value={medical.pfeiffer_test}
                                            onChange={e => handleInputChange(e)}
                                        />
                                    </Col>
                                    <Col md="2">
                                        <Button 
                                            color="secondary" 
                                            href="https://docs.google.com/forms/d/e/1FAIpQLScOfJZpOKaTXd2OQ6RkgpvhByXY51RzeNtlH24rkiQJ1DCZiw/viewform"
                                            target="_blank">Cuestionario
                                        </Button>
                                    </Col>
                                </FormGroup>
                                <Label className="form-control-label">Cuestionario de Yesavage</Label>
                                <FormGroup className="row">
                                    <Col md="10">
                                        <Input
                                            type="number" autoComplete="off"
                                            name="yesavage_test"
                                            value={medical.yesavage_test}
                                            onChange={e => handleInputChange(e)}
                                        />
                                    </Col>
                                    <Col md="2">
                                        <Button 
                                            color="secondary" 
                                            href="https://docs.google.com/forms/d/e/1FAIpQLScmoS5sWNzDJG9k7ssG4D4M_2jCT8IqplAQ8beLLPZNqfPSjg/viewform"
                                            target="_blank">Cuestionario
                                        </Button>
                                    </Col>
                                </FormGroup>
                            </>}
                            {group === 'repo' && <>
                                <FormGroup className="row">
                                    <Col md="6">
                                        <Label className="form-control-label">Imagenes disponibles:</Label>
                                        {images.map((image) => (
                                            <div key={image.foto_id} className="list-group">
                                                <InputGroup>
                                                    <SimpleModal ref={modalRef}/><Button color="danger" size="sm" outline onClick={
                                                        () => setModal({
                                                            title: 'Confirmaci√≥n',
                                                            text: 'Desea eliminar ' + image.foto_nombre + '?',
                                                            type: 2,
                                                            fx: () => removeImage(image.foto_id, image.foto_nombre)
                                                        })
                                                    }><i className="far fa-trash-alt"/></Button>
                                                    <a 
                                                        rel="noopener noreferrer" 
                                                        target="_blank" 
                                                        href={process.env.REACT_APP_API_URL + `${image.url}` + "/" + `${image.foto_nombre}`}>{image.foto_nombre}
                                                    </a>
                                                </InputGroup>
                                            </div>
                                        ))}
                                    </Col>
                                    <Col md="6">
                                        <Card>
                                            <CardHeader>
                                                <FormGroup className="row">
                                                    <Col className="d-flex">
                                                        <Label className="form-control-label">¬øSubir una nueva imagen?</Label>
                                                        <label className="custom-toggle custom-toggle-info ml-3">
                                                            <Input type="checkbox" checked={hasFile} onChange={() => setHasFile(!hasFile)}/>
                                                            <span className="custom-toggle-slider rounded-circle" data-label-off="No" data-label-on="S√≠"/>
                                                        </label>
                                                    </Col>
                                                </FormGroup>
                                            </CardHeader>
                                            {hasFile &&
                                                <CardBody>
                                                    <FormGroup>
                                                        <InputGroup>
                                                            <InputGroupAddon addonType="prepend">
                                                                <Button 
                                                                    type="button" color="primary"
                                                                    onClick={() => saveFile()}>
                                                                    Agregar 
                                                                </Button>
                                                            </InputGroupAddon>
                                                            <Input
                                                                autoComplete="off"
                                                                type="text"
                                                                name="treatment_plan"
                                                                value={imageName}
                                                                onChange={e => setImageName(e.target.value)}
                                                            />
                                                            <InputGroupAddon addonType="append">
                                                                <Button 
                                                                    type="button" color="primary" 
                                                                    onClick={() => {document.getElementById('files').click()}}>
                                                                    Buscar
                                                                </Button>
                                                            </InputGroupAddon>
                                                        </InputGroup>
                                                        <input 
                                                            type="file" 
                                                            accept="image/*"
                                                            id="files" 
                                                            style={{visibility: 'hidden'}} 
                                                            onChange={e => loadFile(e.target.files)}
                                                        />
                                                    </FormGroup>
                                                    {/* <Button className= "float-right" onClick={() => saveFile()} color="primary">Subir</Button> */}
                                                </CardBody>
                                            }
                                        </Card>
                                    </Col>
                                </FormGroup>
                            </>}
                            <div className= "float-right mb-4 mr-2">
                                <Button type="submit" id="id-btn-save" color="primary">Guardar</Button>{' '}
                                {/* <Button onClick={() => {setDisplayModal(!displayModal)}} color="secondary">Cancelar</Button> */}
                            </div>
                        </Form>
                    </CardBody>
                </Card>
            }
        </Container>
    );
};

export default MedicalAnamnesis;
